import React from 'react';
import {QrReader} from 'react-qr-reader';

class App extends React.Component {
  state = {
    result: '',
    showCamera: false
  };

  handleScan = (data) => {
    if (data) {
      this.setState({
        result: data,
        showCamera: false
      });
    }
  };

  handleError = (error) => {
    console.error(error);
  };

  toggleCamera = () => {
    this.setState((prevState) => ({
      showCamera: !prevState.showCamera
    }));
  };

  render() {
    const { result, showCamera } = this.state;

    return (
      <div>
        <h1>QR Code Scanner Web App</h1>

        {showCamera ? (
          <QrReader
            delay={300}
            onError={this.handleError}
            onScan={this.handleScan}
            style={{ width: '100%' }}
          />
        ) : (
          <div>
            <button onClick={this.toggleCamera}>Scan QR Code</button>
            <br />
            <br />
            {result && <h2>{result}</h2>}
          </div>
        )}
      </div>
    );
  }
}

export default App;
