import React, { useState } from 'react';
import {QrReader} from 'react-qr-reader';

function App() {
  const [result, setResult] = useState(null);
  const [showCamera, setShowCamera] = useState(false);

  const handleScan = (data) => {
    if (data) {
      setResult(data);
      setShowCamera(false);
    }
  };

  const handleError = (err) => {
    console.error(err);
  };

  const toggleCamera = () => {
    setShowCamera(!showCamera);
  };

  return (
    <div>
      <h1>QR Code Scanner Web App</h1>
      {showCamera ? (
        <QrReader
          delay={300}
          onError={handleError}
          onScan={handleScan}
          style={{ width: '100%' }}
        />
      ) : (
        <div>
          <button onClick={toggleCamera}>Scan QR Code</button>
          <br />
          <br />
          {result && <h2>{result}</h2>}
        </div>
      )}
    </div>
  );
}

export default App;
